import React from "react";
import './index.css';
import {useFormik} from 'formik';
// TODO: import useFormik from formik library

function App() {
  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },

    onSubmit: values => {
      if (values.email && values.password) alert('Login Successful');
    },

    validate: values => {
      let errors = {};
      let regex = /^[\w\-\.]+@([\w-]+\.)+[\w-]{2,4}$/g;
      if (values.email === '') errors.email = 'Field Required';
      if (values.password === '') errors.password = 'Field Required';
      if (values.email !== regex) errors.email = 'Username should be an email';
      return errors;
    }
  })

  return (
    <div>
      <form onSubmit={formik.handleSubmit}>
        <div>Email</div>
          <input name="email" type="text" onChange={formik.handleChange} value={formik.values.email}/>
          {formik.errors.email ? <div style={{color: 'red'}}>{formik.errors.email}</div> : null}
        <div>Password</div>
          <input name="password" type="text" onChange={formik.handleChange} value={formik.values.password}/>
          {formik.errors.password ? <div style={{color: 'red'}}>{formik.errors.password}</div> : null}
        <br/>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default App;
